æ¢&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    channels = cv2.split(image)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    f_channels = [np.fft.fft2(channel) &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; channel &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; channels]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    fshift_channels = [np.fft.fftshift(f) &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; f &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; f_channels]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; fshift_channels&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;def&lt;/span&gt; &lt;span class=&#34;title function_&#34;&gt;ifft2_image&lt;/span&gt;(&lt;span class=&#34;params&#34;&gt;fshift_channels&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# å¯¹é¢‘ç‡åŸŸçš„æ¯ä¸ªé€šé“è¿›è¡Œé€†å‚…ç«‹å¶å˜æ¢&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    ishift_channels = [np.fft.ifftshift(fshift) &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; fshift &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; fshift_channels]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    img_back_channels = [np.fft.ifft2(ishift) &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; ishift &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; ishift_channels]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    img_back_channels = [np.&lt;span class=&#34;built_in&#34;&gt;abs&lt;/span&gt;(img_back) &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; img_back &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; img_back_channels]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; cv2.merge(img_back_channels)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;def&lt;/span&gt; &lt;span class=&#34;title function_&#34;&gt;randomize_frequency&lt;/span&gt;(&lt;span class=&#34;params&#34;&gt;fshift_channels&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# å¯¹é¢‘ç‡åŸŸçš„æ¯ä¸ªé€šé“è¿›è¡ŒéšæœºåŒ–å¤„ç†&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    randomized_channels = []&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; fshift &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; fshift_channels:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        magnitude = np.&lt;span class=&#34;built_in&#34;&gt;abs&lt;/span&gt;(fshift)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        phase = np.angle(fshift)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        randomized_phase = np.random.uniform(-np.pi, np.pi, phase.shape)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        randomized_fshift = magnitude * np.exp(&lt;span class=&#34;number&#34;&gt;1j&lt;/span&gt; * randomized_phase)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        randomized_channels.append(randomized_fshift)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; randomized_channels&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# è¯»å–å›¾åƒ&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;image = cv2.imread(&lt;span class=&#34;string&#34;&gt;&amp;#x27;path_to_your_image.jpg&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;image = cv2.cvtColor(image, cv2.COLOR_BGR2RGB)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# å°†å›¾åƒè½¬æ¢åˆ°é¢‘ç‡åŸŸ&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;fshift_channels = fft2_image(image)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# åœ¨é¢‘ç‡åŸŸä¸­è¿›è¡ŒéšæœºåŒ–å¤„ç†&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;randomized_fshift_channels = randomize_frequency(fshift_channels)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# å°†é¢‘ç‡åŸŸå›¾åƒè½¬æ¢å›ç©ºé—´åŸŸ&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;reconstructed_image = ifft2_image(randomized_fshift_channels)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;# æ˜¾ç¤ºç»“æœ&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.figure(figsize=(&lt;span class=&#34;number&#34;&gt;12&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;6&lt;/span&gt;))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.subplot(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.title(&lt;span class=&#34;string&#34;&gt;&amp;#x27;Original Image&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.imshow(image)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.subplot(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.title(&lt;span class=&#34;string&#34;&gt;&amp;#x27;Reconstructed Image&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.imshow(np.uint8(reconstructed_image))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;plt.show()&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
