25;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  is-&amp;gt;audio_hw_buf_size = spec.size;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  is-&amp;gt;audioStream = stream_index;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  is-&amp;gt;audio_st = pFormatCtx-&amp;gt;streams[stream_index];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  is-&amp;gt;audio_ctx = codecCtx;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  is-&amp;gt;audio_buf_size = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  is-&amp;gt;audio_buf_index = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;built_in&#34;&gt;memset&lt;/span&gt;(&amp;amp;is-&amp;gt;audio_pkt, &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, &lt;span class=&#34;keyword&#34;&gt;sizeof&lt;/span&gt;(is-&amp;gt;audio_pkt));&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  packet_queue_init(&amp;amp;is-&amp;gt;audioq);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;type&#34;&gt;uint64_t&lt;/span&gt; out_channel_layout = AV_CH_LAYOUT_STEREO;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; out_sample_rate = is-&amp;gt;audio_ctx-&amp;gt;sample_rate;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;type&#34;&gt;int64_t&lt;/span&gt; in_channel_layout = av_get_default_channel_layout(is-&amp;gt;audio_ctx-&amp;gt;channels);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;span class=&#34;class&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;struct&lt;/span&gt; &lt;span class=&#34;title&#34;&gt;SwrContext&lt;/span&gt; *&lt;span class=&#34;title&#34;&gt;audio_c